      console.log(this.newsletter)

      e.preventDefault();

      const res = await this.$axios.post ('https://restapi.fromdoppler.com/accounts/socialmediasanmartin@gmail.com/lists/28595553/subscribers', 
          {
            email: this.newsletter
          }, 
          {
            headers: {
              Authorization: 'token 97C9CD65C9EC6B825CEFAA8D7CC2DF58'
            }
          }
      )

      if(res.status == 200){
        this.newsletter = ''
        this.messageOk = 'Gracias por registrarte en nuestro newsletter.'
      }
    }
  },
  data() {
    return {
      nums:[
